package Pruebas;

public class Prueba_Examen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int Triangulo = 8;
		
		for (int i = 0; i < Triangulo; Triangulo--) {
			
			for (int j = 0 ; j < Triangulo; j++) {
				
				System.out.print("*");
			}
			
			System.out.println();

		}
		

	}

}
